import { useState } from "react";

const FormInput = (props) => {
   const [userInput, setUserInput] = useState({
    'current-saving': 10000,
    'yearly-contribution': 1200,
    'expected-return': 7,
    'duration': 10
  })

  const submitHandler = (event) => {
    event.preventDefault();
    calculateHandler(event);
  }
  const calculateHandler = (userInput) => {
    userInput.preventDefault();
    const yearlyData = [];

    let currentSavings = +userInput.target[0].value;
    const yearlyContribution = +userInput.target[1].value;
    const expectedReturn = +userInput.target[2].value / 100;
    const duration = +userInput.target[3].value;

    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({

        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    props.onSaveFormData(yearlyData);
    console.log(yearlyData);
  };

  const resetHandler = () => {};

  const inputChangeHandler = (input, value) => {
    setUserInput()
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            onChange={(e) => {
              inputChangeHandler("current-savings", e.target.value);
            }}
            type="number"
            id="current-savings"
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            onChange={(e) => {
              inputChangeHandler("yearly-contribution", e.target.value);
            }}
            type="number"
            id="yearly-contribution"
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            onChange={(e) => {
              inputChangeHandler("expected-return", e.target.value);
            }}
            type="number"
            id="expected-return"
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            onChange={(e) => {
              inputChangeHandler("duration",
               e.target.value);
            }}
            type="number"
            id="duration"
          />
        </p>
      </div>
      <p className="actions">
        <button onClick={resetHandler} type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default FormInput;
